components/category/nav-category-item:
{{#if children}}
    <li class="navList-item has-subMenu">
        <a class="navList-action has-subMenu{{#if is_active}} is-active{{/if}}" href="{{url}}">{{name}}</a>
        <a class="navList-toggle{{#if is_active}} is-open{{/if}}" data-collapsible="navList-category-{{id}}"><i class="icon" aria-hidden="true"><svg><use xlink:href="#icon-add" /></svg></i></a>
        <ul class="navList{{#if is_active}} is-open{{/if}}" id="navList-category-{{id}}">
            {{#each children}}
                {{> components/category/nav-category-item currentCategory=../currentCategory currentId=../currentId}}
            {{/each}}
        </ul>
    </li>
{{else}}
    <li class="navList-item">
        <a class="navList-action{{#if is_active}} is-active{{/if}}" href="{{url}}">{{name}}</a>
    </li>
{{/if}}

{{#if currentCategory}}
    {{#if currentCategory.id '==' id}}
        {{#if (merge currentCategory foundCurrentCategory=true)}}{{/if}}
    {{/if}}
{{/if}}

components/category/shop-by-price:
{{#if theme_settings.shop_by_price_visibility}}
    <div class="sidebarBlock">
        <h5 class="sidebarBlock-heading">{{lang 'category.shop_by_price'}}</h5>
        <ul class="navList">
            {{#each shop_by_price}}
            <li class="navList-item">
                <a class="navList-action {{#if selected }} is-active {{/if}}" href="{{url}}" alt="{{low.formatted}} - {{high.formatted}}" title="{{low.formatted}} - {{high.formatted}}">{{low.formatted}} - {{high.formatted}}</a>
            </li>
            {{/each}}

            {{#any shop_by_price selected=true}}
                <li class="navList-item">
                    <a href="{{category_url}}" class="navList-action">
                        {{lang 'category.reset'}}
                    </a>
                </li>
            {{/any}}
        </ul>
    </div>
{{/if}}

components/chiara/banners/count-standard:
{{#if (merge banners top_count=0)}}{{/if}}
{{#if banners.top_metadata}}
    {{#each banners.top_metadata}}
        {{#unless (occurrences this.content 'data-remote-banner-position')}}
            {{#unless (occurrences this.content 'data-local-banner-position')}}
                {{#if (merge ../../../banners top_count=(add ../../../banners.top_count 1))}}{{/if}}
            {{/unless}}
        {{/unless}}
    {{/each}}
{{/if}}

components/common/currency-selector:
{{#if currency_selector.currencies.length '>' 1}}
    <li class="navUser-item navUser-item--currencies">
        <a class="navUser-action navUser-action--currencySelector has-dropdown" href="#" data-dropdown="currencySelection" data-options="align:top" aria-controls="currencySelection" aria-expanded="false">{{lang 'common.currency' code=currency_selector.active_currency_code}} <i class="icon" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i></a>
        <ul class="dropdown-menu" id="currencySelection" data-dropdown-content aria-hidden="true" tabindex="-1">
            {{#each currency_selector.currencies}}
            <li class="dropdown-menu-item">
                <a href="{{{switch_url}}}">
                    {{#if is_active}}
                    <strong>{{name}}</strong>
                    {{else}}
                    {{name}}
                    {{/if}}
                </a>
            </li>
            {{/each}}
        </ul>
    </li>
{{/if}}

components/common/footer:
{{#if banners.bottom_metadata}}
