components/chiara/products/carousel:
{{!
    Parameters:
    - products
    - columns
    - columns_sm
    - columns_md
}}
<section class="productCarousel"
    data-slick='{
        "dots": false,
        "arrows": true,
        "infinite": true,
        "mobileFirst": true,
        "slidesToShow": {{#if columns_sm}}{{columns_sm}}{{else}}2{{/if}},
        "slidesToScroll": 1,
        "autoplay": true,
        "autoplaySpeed": 3000,
        "useTransform": false, {{! Fix for IE10+ }}
        "responsive": [
            {
                "breakpoint": 1260,
                "settings": {
                    "slidesToScroll": 1,
                    "slidesToShow": {{ columns }}
                }
            },
            {
                "breakpoint": 550,
                "settings": {
                    "slidesToScroll": 1,
                    "slidesToShow": {{#if columns_md}}{{columns_md}}{{else}}2{{/if}}
                }
            }
        ]
    }'
>
    {{#each products}}
    <div class="productCarousel-slide">
        {{> components/products/card theme_settings=../theme_settings customer=../customer show_wishlist=../settings.show_wishlist}}
    </div>
    {{/each}}
</section>

components/chiara/products/grid:
{{!
    Parameters:
    - products
    - columns
    - columns_sm
    - columns_md
}}
<ul class="productGrid
    {{#if columns}} productGrid--maxCol{{columns}}{{/if}}
    {{#if columns_sm}} productGrid--sm-maxCol{{columns_sm}}{{/if}}
    {{#if columns_md}} productGrid--md-maxCol{{columns_md}}{{/if}}
    ">
    {{#each products}}
    <li class="product">
        {{>components/products/card show_compare=../show_compare show_rating=../settings.show_product_rating theme_settings=../theme_settings customer=../customer show_wishlist=../settings.show_wishlist}}
    </li>
    {{/each}}
</ul>

components/chiara/sections/categories-li:
{{#or theme_settings.home_cats_show theme_settings.home_cats_show_sm theme_settings.home_cats_show_md}}
    {{#if (merge this home_cats_index=0)}}{{/if}}
    {{#or theme_settings.home_cats_id theme_settings.home_cats_count}}
        {{#if theme_settings.home_cats_count '!=' '0'}}
            {{#block "chiara-categories-list"}}
            <div class="chiara-section chiara-section--categoriesList
                {{#unless theme_settings.home_cats_show}}u-hiddenVisually-desktop{{/unless}}
                {{#unless theme_settings.home_cats_show_md}}u-hiddenVisually-tablet{{/unless}}
                {{#unless theme_settings.home_cats_show_sm}}u-hiddenVisually-mobile{{/unless}}">
                <div class="chiara-categoriesList
                    chiara-categoriesList--count-{{theme_settings.home_cats_count}}
                    {{#if theme_settings.home_cats_show_banner}}chiara-categoriesList--hasBanner{{/if}}
                    {{#if theme_settings.home_cats_style}}chiara-categoriesList--style-{{theme_settings.home_cats_style}}{{/if}}">
                    {{#if theme_settings.home_cats_heading}}
                        <h3 class="page-heading">{{theme_settings.home_cats_heading}}</h3>
                    {{/if}}
                    {{#if theme_settings.home_cats_subheading}}
                        <p class="page-subheading">{{theme_settings.home_cats_subheading}}</p>
                    {{/if}}
                    <div class="chiara-categoriesList-list">
                        {{#if theme_settings.home_cats_id}}
                            {{#each (split theme_settings.home_cats_id ",")}}
                                {{#each ../categories}}
                                    {{#if id '===' ../this}}
                                        <div class="chiara-categoriesList-item">
                                            {{> components/chiara/sections/categories-list-category category=this theme_settings=../../../theme_settings index=../../../this.home_cats_index}}
                                        </div>
                                        {{#if (merge ../../../this home_cats_index=(add ../../../this.home_cats_index 1))}}{{/if}}
                                    {{/if}}
                                    {{#each children}}
                                        {{#if id '===' ../../this}}
                                            <div class="chiara-categoriesList-item">
                                                {{> components/chiara/sections/categories-list-category category=this theme_settings=../../../../theme_settings index=../../../../this.home_cats_index}}
                                            </div>
                                            {{#if (merge ../../../../this home_cats_index=(add ../../../../this.home_cats_index 1))}}{{/if}}
                                        {{/if}}
                                    {{/each}}
                                {{/each}}
                            {{/each}}
                        {{else}}
                            {{#each (limit categories theme_settings.home_cats_count)}}
                                <div class="chiara-categoriesList-item">
                                    {{> components/chiara/sections/categories-list-category category=this theme_settings=../theme_settings index=../this.home_cats_index}}
                                </div>
                                {{#if (merge ../this home_cats_index=(add ../this.home_cats_index 1))}}{{/if}}
                            {{/each}}
                        {{/if}}
                    </div>
                </div>
            </div>
            {{/block}}
        {{/if}}
    {{/or}}
{{/or}}

{{#if theme_settings.home_cats_show_banner}}
    {{#if index '<' 10}}
    <div class="chiara-categoriesList-banner"><img src="{{cdn 'img/loading.svg'}}" data-src="{{get (concat 'home_cats_banner' index) theme_settings}}" alt="{{category.name}}" class="lazyload"></div>
    {{else}}
        <div class="chiara-categoriesList-banner"><img src="{{cdn 'img/loading.svg'}}" data-src="/product_images/uploaded_images/{{concat 'home-cat' (add index 1)}}.jpg?c=2" alt="{{category.name}}" class="lazyload"></div>
    {{/if}}
{{/if}}
<div class="chiara-categoriesList-content">
    <h3 class="chiara-categoriesList-name"><a href="{{category.url}}">{{category.name}}</a></h3>
    {{#if theme_settings.home_cats_sub_count '>' 0}}
    {{#if category.children}}
    <ul class="chiara-categoriesList-subList">
        {{#each (limit category.children theme_settings.home_cats_sub_count)}}
        <li class="chiara-categoriesList-subList-item"><a href="{{url}}">{{name}}</a></li>
        {{/each}}
    </ul>
    {{/if}}
    {{/if}}
</div>

